import { View, Text, FlatList, TextInput, Button } from "react-native";
import React, { useEffect, useState } from "react";
import firebase from "firebase/compat/app";
import "firebase/compat/firestore";
import "firebase/compat/auth";

import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { fetchUsersData } from "../../redux/actions";

const Comments = (props) => {
  const [comments, setComments] = useState([]);
  const [postId, setPostId] = useState("");
  const [comment, setComment] = useState("");
  useEffect(() => {
    function matchUserToComment(comments) {
      for (let i = 0; i < comments.lentgh; i++) {
        if (comments[i].hasOwnProperty("user")) {
          continue;
        }
        const user = props.user.find((x) => x.uid === comments[i].user);
        if (user == undefined) {
          props.fetchUsersData(comments[i].user, false);
        } else {
          comments[i].user = user;
        }
      }
      setComments(comments);
    }
    if (props.route.params.postId !== postId) {
      firebase
        .firestore()
        .collection("posts")
        .doc(props.route.params.uid)
        .collection("userPosts")
        .doc(props.route.params.postId)
        .collection("comments")
        .get()
        .then((snapshot) => {
          let comments = snapshot.docs.map((doc) => {
            const data = doc.data();
            const id = doc.id;
            return { id, ...data };
          });
          matchUserToComment(comments);
        });
      setPostId(props.route.params.postId);
    } else {
      matchUserToComment(comments);
    }
  }, [props.route.params.postId, props.users]);

  const onCommentSend = () => {
    firebase
      .firestore()
      .collection("posts")
      .doc(props.route.params.uid)
      .collection("userPosts")
      .doc(props.route.params.postId)
      .collection("comments")
      .add({
        userid: firebase.auth().currentUser.uid,
        comment,
      });
  };

  return (
    <View>
      <FlatList
        numColumns={1}
        horizontal={false}
        data={comments}
        renderItem={({ item }) => (
          <View>
            {item.user !== undefined ? <Text>{item.user.username}</Text> : null}
            <Text>{item.comment} </Text>
          </View>
        )}
      />
      <View>
        <TextInput
          placeholder="Write a comment..."
          onChangeText={(comment) => setComment(comment)}
        />
        <Button title="Send" onPress={() => onCommentSend()} />
      </View>
    </View>
  );
};

const mapStateToProps = (store) => ({
  users: store.usersState.users,
});
const mapDispatchProps = (dispatch) =>
  bindActionCreators({ fetchUsersData }, dispatch);

export default connect(mapStateToProps, mapDispatchProps)(Comments);
