import firebase from "firebase/compat/app";
import "firebase/compat/firestore";
import "firebase/firestore";
import "firebase/auth";
import {
  USER_STATE_CHANGE,
  USER_POSTS_STATE_CHANGE,
  USERS_POSTS_STATE_CHANGE,
  USER_FOLLOWS_STATE_CHANGE,
  USERS_DATA_STATE_CHANGE,
} from "../constants/index.js";

export function fetchUser() {
  return (dispatch) => {
    firebase
      .firestore()
      .collection("users")
      .doc(firebase.auth().currentUser.uid)
      .get()
      .then((snapshot) => {
        if (snapshot.exists) {
          dispatch({ type: USER_STATE_CHANGE, currentUser: snapshot.data() });
        } else {
          console.log("User doesn't exist");
        }
      });
  };
}
export function fetchUserPosts() {
  return (dispatch) => {
    firebase
      .firestore()
      .collection("posts")
      .doc(firebase.auth().currentUser.uid)
      .collection("userPosts")
      .orderBy("created_at", "desc")
      .get()
      .then((snapshot) => {
        let posts = snapshot.docs.map((doc) => {
          const data = doc.data();
          const id = doc.id;
          return { id, ...data };
        });
        console.log(posts);
        dispatch({ type: USER_POSTS_STATE_CHANGE, posts });
      });
  };
}
export function fetchUserFollows() {
  return (dispatch) => {
    firebase
      .firestore()
      .collection("follows")
      .doc(firebase.auth().currentUser.uid)
      .collection("userFollows")
      .onSnapshot((snapshot) => {
        let follows = snapshot.docs.map((doc) => {
          const id = doc.id;
          return id;
        });
        console.log(follows);
        dispatch({ type: USER_FOLLOWS_STATE_CHANGE, follows });
        for (let i = 0; i < follows.length; i++) {
          dispatch(fetchUsersData(follows[i]));
        }
      });
  };
}

export function fetchUsersData(uid) {
  return (dispatch, getState) => {
    const found = getState().usersState.users.some((el) => el.uid === uid);

    if (!found) {
      firebase
        .firestore()
        .collection("users")
        .doc(uid)
        .get()
        .then((snapshot) => {
          if (snapshot.exists) {
            let user = snapshot.data();
            user.uid = snapshot.id;

            dispatch({ type: USERS_DATA_STATE_CHANGE, user });
            dispatch(fetchUsersFollowsPosts(user.id));
          } else {
            console.log("User doesn't exist");
          }
        });
    }
  };
}
export function fetchUsersFollowsPosts(uid) {
  return (dispatch, getState) => {
    firebase
      .firestore()
      .collection("posts")
      .doc(uid)
      .collection("userPosts")
      .orderBy("created_at", "desc")
      .get()
      .then((snapshot) => {
        console.log(snapshot.query);
        const uid = snapshot.query._delegate._query.gt.path.segments[1];
        console.log(uid);
        const user = getState().usersState.users.find((el) => el.uid === uid);

        let posts = snapshot.docs.map((doc) => {
          const data = doc.data();
          const id = doc.id;
          return { id, ...data, user };
        });
        console.log(posts);
        dispatch({ type: USERS_POSTS_STATE_CHANGE, posts, uid });
        console.log(getState());
      });
  };
}
